package MathodDemo;

public class mathod3 {

	public static void number(int init,int limit) {
		// TODO Auto-generated method stub
		//입력값
		int i = init;
		while(i< limit)
		{
			System.out.println(i);
			i++;
		}
		
	}
	public static void main(String [] args) {
		//number(3);
		number(1, 5);
	}
	
	

}
